examples:
addLayersControls
{
    relativeSizes true;
    layers
    {
        "(wall.*)" { nSurfaceLayers 3; }
    }
    expansionRatio 1.2;
    finalLayerThickness 0.3;
    minThickness 0.1;
}

boundaryField
{
    inlet
    {
        type            fixedValue;
        value           uniform (1 0 0);
    }
}

type            codedFixedValue;
value           uniform 0;
name            myBC;
code
#{
    operator==(vector(sin(this->db().time().value()), 0, 0));
#};

divSchemes
{
    default         none;
    div(phi,U)      Gauss linearUpwind grad(U);
}

type            externalWallHeatFluxTemperature;
mode            flux;
q               uniform 1000;
value           uniform 300;

type            externalWallHeatFluxTemperature;
mode            coefficient;
h               uniform 10;
Ta              uniform 300;

type            fan;
file            "fanCurve";
jump            uniform 0;
value           uniform 0;

FoamFile
{
    version     2.0;
    format      ascii;
    class       dictionary;
    object      controlDict;
}

functions
{
    forces
    {
        type            forces;
        libs            ("libforces.so");
        patches         (wall);
        ...
    }
}

solver           GAMG;
smoother         DICGaussSeidel;
tolerance        1e-07;
relTol           0.01;
nCellsInCoarsestLevel 10;

type            inletOutlet;
inletValue      uniform 300;
value           uniform 300;

type            interstitialInletVelocity;
inletVelocity   uniform (1 0 0);
alpha           alpha;

LES
{
    model           Smagorinsky;
    turbulence      on;
    printCoeffs     on;
}

meshQualityControls
{
    maxNonOrtho 65;
    maxBoundarySkewness 20;
    maxInternalSkewness 4;
    maxConcave 80;
    minVol 1e-13;
    minTetQuality 1e-15;
}

type            mixed;
refValue        uniform 0;
refGradient     uniform 0;
valueFraction   uniform 1;

div(phi,Yi_h)   Gauss multivariateSelection
{
    O2  limitedLinear01 1;
    CH4 limitedLinear01 1;
    N2  limitedLinear01 1;
    H2O limitedLinear01 1;
    CO2 limitedLinear01 1;
    h   limitedLinear 1;
}

type            outletInlet;
outletValue     uniform 0;
value           uniform 0;

type            outletMappedUniformInlet;
outletPatch     outlet;
phi             phi;

solver          PBiCG;
preconditioner  DILU;
tolerance       1e-05;
relTol          0.1;

solver          PBiCGStab;
preconditioner  DILU;
tolerance       1e-05;
relTol          0;

solver          PCG;
preconditioner  DIC;
tolerance       1e-07;
relTol          0.01;

PIMPLE
{
    nOuterCorrectors 1;  // PISO mode
    nCorrectors     2;
    nNonOrthogonalCorrectors 0;
}

PIMPLE
{
    nOuterCorrectors 50;  // Iterative mode
    nCorrectors     1;
    residualControl
    {
        p               1e-4;
        U               1e-4;
    }
}

type            pressureDirectedInletOutletVelocity;
inletDirection  uniform (1 0 0);
value           uniform (0 0 0);

type            prghTotalPressure;
p0              uniform 1e5;
U               U;
phi             phi;

RAS
{
    model           kEpsilon;
    turbulence      on;
    printCoeffs     on;
}

refinementSurfaces
{
    wing
    {
        level (2 3);
        patchInfo { type wall; }
    }
}

relaxationFactors
{
    fields
    {
        p               0.3;  // Pressure typically 0.3
    }
    equations
    {
        U               0.7;  // Momentum typically 0.7
        k               0.7;
        epsilon         0.7;
    }
}

SIMPLE
{
    nNonOrthogonalCorrectors 0;
    consistent      yes;  // SIMPLEC
    residualControl
    {
        p               1e-4;
        U               1e-4;
    }
}

solver          smoothSolver;
smoother        symGaussSeidel;
tolerance       1e-05;
relTol          0.1;

snapControls
{
    nSmoothPatch 3;
    tolerance 3.0;
    nSolveIter 30;
    nRelaxIter 5;
    nFeatureSnapIter 10;
}

type            totalPressure;
p0              uniform 1e5;
U               U;
phi             phi;
value           uniform 1e5;
